    {
        Slug = "example-app",
        ProtocolProvider = nameProviderOauth2.Id,
    });

    var app_access = new Authentik.PolicyBinding("app-access", new()
    {
        Target = nameApplication.Uuid,
        Policy = policy.Id,
        Order = 0,
